def import_questions():
    """导入马克思主义基本原理题目到数据库"""
    with app.app_context():
        # 创建数据库表（如果不存在）
        db.create_all()
        
        # 导入题目
        for i, question_data in enumerate(marx_questions, 1):
            question_text, options, correct_answer, explanation, difficulty, subject = question_data
            # 创建单选题，correct_answer是选项索引
            question = Question(
                question_text=question_text,
                question_type='single_choice',
                options=str(options),
                correct_answer=str([options[correct_answer]]),
                explanation=explanation,
                difficulty=difficulty,
                subject=subject
            )
            db.session.add(question)
            
            # 每导入10道题提交一次，避免内存占用过大
            if i % 10 == 0:
                db.session.commit()
                print(f"已导入{ i }道马克思主义基本原理题目")
        
        # 提交剩余题目
        db.session.commit()
        print(f"全部导入完成，共导入{ len(marx_questions) }道马克思主义基本原理题目")

if __name__ == "__main__":
    import_questions()
